<?php/**My Test Application*//** * I am the connection to the database * @version CodeGen - 1.8 * @author CodeGen - Jonnie Spratley (http://jonniespratley.com/code) * * @name codegenConnection.php */class codegenConnection{	private $link;		/**	 * I create a new connection to a database	 *	 * @return [link] a link to the connection	 */	public function __construct()	{		$host = 'localhost';		$user = 'root';		$pass = 'fred';				$link = new mysqli ( $host, $user, $pass );				return $this->setLink ( $link );	}		public function executeAndReturn( $sql )	{		$array = array ();		$result = $this->link->query ( $sql );				if ( $result )		{			/* fetch associative array */			while ( $row = $result->fetch_assoc () )			{				$array [] = $row;			}						/* free result set */			$result->close ();		}				/* close connection */		//$this->link->close();				return $array;	}		public static function get_handle()	{		static $db = null;		if ( ! isset ( $db ) )		{			$db = new Connection ( );		}		return $db->link;	}		/**	 * I execute a query on the database	 *	 * @param [string] $query	 * @return [result] the result	 */	public function execute( $query )	{		return $this->link->query ( $query );	}		static public function escape( $value )	{		$escaped = $value;				if ( ! is_numeric ( $value ) )		{			$escaped = '"' . ( $value ) . '"';		}				return $escaped;	}		static public function trimSQL( $sql )	{		return substr ( $sql, 0, strlen ( $sql ) - 2 );	}		/**	 * @return unknown	 */	public function getLink()	{		return $this->link;	}		/**	 * @param unknown_type $link	 */	public function setLink( $link )	{		$this->link = $link;	}}?>       