<?php/** * I am the connection to the database *  *  * * @version CodeGen - 1.9.3 * @author CodeGen - Jonnie Spratley - https://github.com/jonniespratley/ * * @name Connection.php */class Connection{	private $link;		/**	 * I create a new connection to a database	 *	 * @return [link] a link to the connection	 */	public function __construct()	{		$host = "";		$user = "";		$pass = "";				$link = new mysqli ( $host, $user, $pass );				return $this->setLink ( $link );	}		public function executeAndReturn( $sql )	{		$array = array ();		$result = $this->link->query ( $sql );				if ( $result )		{			while ( $row = $result->fetch_assoc () )			{				$array [] = $row;			}			$result->close ();		}		return $array;	}		public static function get_handle()	{		static $db = null;		if ( ! isset ( $db ) )		{			$db = new Connection( );		}		return $db->link;	}		/**	 * I execute a query on the database	 *	 * @param [string] $query	 * @return [result] the result	 */	public function execute( $query )	{		return $this->link->query ( $query );	}		static public function escape( $value )	{		$escaped = $value;				if ( ! is_numeric ( $value ) )		{			$escaped = '"' . ( $value ) . '"';		}				return $escaped;	}		static public function trimSQL( $sql )	{		return substr ( $sql, 0, strlen ( $sql ) - 2 );	}		/**	 * @return database handle	 */	public function getLink()	{		return $this->link;	}		/**	 * @param database handle $link	 */	public function setLink( $link )	{		$this->link = $link;	}}?>       